mapper.select:
select * from ( select , ,
from course c,teacher t,dept d where c.tno = t.tno and c.dno = d.dno and t.del_flag = 0 and d.del_flag = 0 and c.del_flag = 0 ) a left join ( select ,
from course c,student s,cs cs where c.cno = cs.cno and s.sno = cs.sno and s.del_flag = 0 ) b on a.`course.cno` = b.`course.cno`
where a.`course.cno` = #{cno,jdbcType=VARCHAR}

select * from ( select , ,
from course c,teacher t,dept d where c.tno = t.tno and c.dno = d.dno and t.del_flag = 0 and d.del_flag = 0 and c.del_flag = 0 ) a left join ( select ,
from course c,student s,cs cs where c.cno = cs.cno and s.sno = cs.sno and s.del_flag = 0 ) b on a.`course.cno` = b.`course.cno`

select * from ( select , ,
from course c,teacher t,dept d where c.tno = t.tno and c.dno = d.dno and t.del_flag = 0 and d.del_flag = 0 and c.del_flag = 0 ) a left join ( select ,
from course c,student s,cs cs where c.cno = cs.cno and s.sno = cs.sno and s.del_flag = 0 ) b on a.`course.cno` = b.`course.cno`
and b.`student.sno` = #{student.sno,jdbcType=VARCHAR}
and b.`course.cno` = #{cno,jdbcType=VARCHAR}
and b.`course.type` = #{type,jdbcType=VARCHAR}
and b.`course.tno` = #{tno,jdbcType=VARCHAR}

mapper.update:
update course
set del_flag = 1
where cno = #{cno,jdbcType=VARCHAR}

mapper.insert:
insert into course (cno, name, tno, 
  dno, date, place, credit, 
  total, type, del_flag
  )
values (#{cno,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{tno,jdbcType=VARCHAR}, 
  #{dno,jdbcType=VARCHAR}, #{date,jdbcType=VARCHAR}, #{place,jdbcType=VARCHAR}, #{credit,jdbcType=VARCHAR}, 
  #{total,jdbcType=INTEGER}, #{type,jdbcType=VARCHAR}, #{delFlag,jdbcType=INTEGER}
  )
