mapper.select:
select * from ( select ,
from student s,dept d where s.dno = d.dno and s.del_flag = 0 and d.del_flag = 0 ) a left join ( select ,
from cs cs, course c, student s where s.sno = cs.sno and cs.cno = c.cno and c.del_flag = 0 and s.del_flag = 0 ) b on a.`student.sno` = b.`student.sno`
where a.`student.sno` = #{sno,jdbcType=VARCHAR}

select * from ( select ,
from student s,dept d where s.dno = d.dno and s.del_flag = 0 and d.del_flag = 0 ) a left join ( select ,
from cs cs, course c, student s where s.sno = cs.sno and cs.cno = c.cno and c.del_flag = 0 and s.del_flag = 0 ) b on a.`student.sno` = b.`student.sno`

mapper.update:
update student
set del_flag = 1
where sno = #{sno,jdbcType=VARCHAR}
